function getCollections(){$.getJSON("/albums",function(h){var b=[];var g=Object.keys(h);for(var e=0,d=g.length;e<d;e++){var f=[];for(var a=0,c=h[g[e]].albums.length;a<c;a++){f.push('<li><a href="/gallery.html?album='+h[g[e]].albums[a].id+"&title="+h[g[e]].albums[a].title.replace(/_/gi," ")+'"><i class="icon-camera"></i>&nbsp;'+h[g[e]].albums[a].title.replace(/_/gi," ")+"</a></li>")}if(h[g[e]].title!=="Root"){b.unshift('<li class="expanded"><a href=#><i class="icon-folder-close"></i>&nbsp;'+h[g[e]].title+'</a><ul class="sublist">'+f.join("")+"</ul></li>")}else{b.push(f.join(""))}}slideOut(b)})}function getSets(){var a=[];$.getJSON("/albums",function(e){var d=Object.keys(e);for(var c=0,b=d.length;c<b;c++){a.push('<li><a href="/gallery.html?album='+e[d[c]].id+"&title="+e[d[c]].title.replace(/_/gi," ")+'"><i class="icon-camera"></i>&nbsp;'+e[d[c]].title.replace(/_/gi," ")+"</a></li>")}slideOut(a)})}function slideOut(a){$("#slidecontent ul").append(a.join(""));$("#slideclick").on("click",function(){if($(this).parent().hasClass("popped")){$(this).parent().animate({left:"-222px"},{queue:false,duration:500}).removeClass("popped");if($(document).attr("title")==="Photo Gallery"){$("#slideclick").pointPoint()}}else{$(this).parent().animate({left:"0px"},{queue:false,duration:500}).addClass("popped");if($(document).attr("title")==="Photo Gallery"){$("#slideclick").pointPoint().destroyPointPoint()}}});$("ul ul").hide();$("ul li.expanded").removeAttr("href");$("ul li.expanded > a").click(function(b){$(this).parent().find("ul").toggle("slow");$("i.icon-folder-close").toggleClass("icon-folder-open")});if($(document).attr("title")==="Photo Gallery"){$("#slideclick").pointPoint()}};